import React,{useState} from "react";
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import { useInputArray } from "../Contexts/InputArrayContext"; 

export const ListItems=()=>{
    var [isChecked,setChecked]=useState(false);
    const inputArray=useInputArray()
    
    function deleteTask(item : string){
        console.log("dd")
        inputArray.dispatch({type:"Delete",task:item})
    }
    type makeListItemType={
        item:string;
        index:number
    }
    function makeListItem({item,index}:makeListItemType){
        return <div className="liDiv"><li key={index}  style={{textDecoration:isChecked?"line-through":"none",color:"black"}} onClick={()=>setChecked(!isChecked)}>{item}</li> <IconButton aria-label="delete" onClick={()=>deleteTask(item)}>
        <DeleteIcon />
      </IconButton>
      </div>
    }
  
    return <div className="listitemdiv">
        <ul>
            {inputArray.TasksList.map((item,index)=>makeListItem({item,index}))}
        </ul>
    </div>
}
