import React,{useState} from "react";
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import { InputArea } from "./InputArea";
export const Home=()=>{
    const [inputArea,setInputArea]=useState(false);

    return(
        <div className="homeContainer" style={{position:"relative"}}>
            <h1 style={{textAlign:"center",marginTop:"30px"}}>To Do List</h1>
            {inputArea?<InputArea/>:null}



<Fab color="secondary" size="small" aria-label="add" onClick={()=>setInputArea(true)} style={{position:"absolute",bottom:"15px",right:"15px"}}>
{!inputArea?<AddIcon />:null}
  
</Fab>
        </div>
    )
}
