import CancelIcon from '@mui/icons-material/Cancel';
import AddIcon from '@mui/icons-material/Add';

import Fab from '@mui/material/Fab';
type InputAreaProps={
    onClose:()=>void,
}

export const InputArea=(props : InputAreaProps)=>{
    return(
        <div className="inputAreaContainer">
            <CancelIcon style={{position:"absolute",top:"15px",right:"15px",color:"white"}} onClick={props.onClose}/>
            <p >Enter New Task !</p>
            <div style={{display:"flex",justifyContent:"flex-start",alignItems:"center",flexDirection:"column",width:"100%",height:"80%",marginTop:"40px"}}>
                <textarea placeholder='Enter Your Task Here'></textarea>
                <Fab variant="extended">
  <AddIcon sx={{ mr: 1 }} />
  Add New Task
</Fab>
</div>
        </div>
    )
}
