import React,{useState} from "react";
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import { useInputArray } from "../Contexts/InputArrayContext"; 

export const ListItems=()=>{
    var [isChecked,setChecked]=useState(false);
    const inputArray=useInputArray()
    
    function deleteTask(item : string){
        console.log("dd")
        inputArray.dispatch({type:"Delete",task:item})
    }
    
    type makeListItemType={
        item:string;
        index:number
    }
    function makeListItem({item,index}:makeListItemType){
        
        return (
        <div className="lidiv">
        <div className="liItem"><div key={index}  style={{textDecoration:isChecked?"line-through":"none",color:"white",width:"80%" ,wordWrap:"break-word"}} onClick={()=>setChecked(!isChecked)}>{item}</div> <IconButton aria-label="delete" onClick={()=>deleteTask(item)}>
        <DeleteIcon style={{color:"white"}} />
      </IconButton>
      </div>
      </div>
      )
    }
  
    return <div className="allTaskDiv">
       
            {inputArray.TasksList.map((item,index)=>makeListItem({item,index}))}
        
    </div>
}
