import React, { useState, useReducer, useEffect, createContext, useContext } from "react";

const Array = ["wedw", "edwed", "wedwed"]
type InputArrayType = {
    TasksList: string[];
    dispatch:React.Dispatch<TaskListAction>
}
type InputArrayProviderType = {
    children: React.ReactNode
}
type TaskListAction = {
    type: "Add" | "Delete";
    task: string;
}
const InputArrayContext = createContext<InputArrayType | undefined>(undefined);

const TasksListReducer = (state: string[], action: TaskListAction):string[] => {
    switch (action.type) {
        case "Add":
            return [...state,action.task];
        case "Delete":
            return state.filter(item => item !== action.task)
    }
}

export const InputArrayProvider = ({ children }: InputArrayProviderType) => {
    const [TasksList, dispatch] = useReducer(TasksListReducer, [])
    return(
        <InputArrayContext.Provider value={{TasksList,dispatch}}>
            {children}
        </InputArrayContext.Provider>
    )
}
export const useInputArray=() : InputArrayType=>{
    const context=useContext(InputArrayContext);
    if (!context) {
        throw new Error('error with Input provider');
      }
      return context;
}
