import React, { useState } from "react";
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';
import { useInputArray } from "../Contexts/InputArrayContext";

export const ListItems = () => {
    var [isChecked, setChecked] = useState(false);
    const inputArray = useInputArray()

    function deleteTask(item: string) {
        console.log("dd")
        inputArray.dispatch({ type: "Delete", task: item })
    }

    type makeListItemType = {
        item: string;
        index: number
    }
    function toggleLineThrough(itemid: string) {
        const element = document.getElementById(itemid);
        if (element) {
            if (element.style.textDecoration === "none") {
                element.style.textDecoration = "line-through"
            }
            else {
                element.style.textDecoration = "none"
            }
        }

    }
    function makeListItem({ item, index }: makeListItemType) {
        const itemId = `item${index}`
        return (
            <div className="lidiv">
                <div className="liItem"><div key={index} id={itemId} style={{ color: "white", width: "80%", wordWrap: "break-word" }} onClick={()=>toggleLineThrough(itemId)}>{item}</div> <IconButton aria-label="delete" onClick={() => deleteTask(item)}>
                    <DeleteIcon style={{ color: "white" }} />
                </IconButton>
                </div>
            </div>
        )
    }

    return <div className="allTaskDiv">

        {inputArray.TasksList.map((item, index) => makeListItem({ item, index }))}

    </div>
}
